<?php 

    use Dompdf\Dompdf;


    if(!empty($_GET['disconnectBtn'])) {
        unset($_SESSION['id'], $_SESSION['login']);
        header('Location: ../../index.php');
    }elseif(!empty($_GET['extractBtn'])) {
        
        require '../../core/vendor/autoload.php';

        ob_end_clean();
        
        $connect = connectDB();
        $query = $connect->query('SELECT * FROM '.DB_PREFIX.'USER WHERE email="'.$_SESSION['id'].'"');
        $result = $query->fetch();

        $newsletterTOF = ($result['newsletter'] == 1) ? 'Oui' : 'Non';
        switch($result["gender"]) {
            case 0:
                $gender = "Homme";
                break;
            case 1:
                $gender = "Femme";
                break;
            case 2:
                $gender = "Autres";
                break;
        }

        switch($result['status']) {
            case 0:
                $role = 'Lecteur';
                break;
            case 1:
                $role = 'Modérateur';
                break;
            case 2:
                $role = 'Administrateur';
                break;
        }

        $query2 = $connect->query('SELECT COUNT(id) FROM '.DB_PREFIX.'ARTICLE WHERE author='.$result['id']);
        $result2 = $query2->fetch();

        $query3 = $connect->query('SELECT COUNT(id) FROM '.DB_PREFIX.'DONATE WHERE author='.$result['id']);
        $result3 = $query3->fetch();

        $html = '
        <style>
        .table { width: 100%; margin-bottom: 50px; }
        .table th, .table td { border-top: 1px solid #5F85DB; padding-top: 0.75rem; padding-bottom: 0.75rem; vertical-align: top; }
        .table td { padding-left: 10px;}
        .last { border-bottom: 1px solid #5F85DB; }
        .table .table { background-color: #B4B4B4; }
        </style>
        <center>
        <img src="data:image/png;base64," alt="Logo WikiFine">
        <h1 style="color: #5F85DB;">Informations de votre profil</h1>
        <table class="table table-striped">
            <tbody>
                <tr>
                    <th scope="row">Pseudo</th>
                    <td>'.$result['pseudo'].'</td>
                </tr>
                <tr>
                    <th scope="row">Prénom</th>
                    <td>'.$result['firstname'].'</td>
                </tr>
                <tr>
                    <th scope="row">Nom</th>
                    <td>'.$result['lastname'].'</td>
                </tr>
                <tr>
                    <th scope="row">Email</th>
                    <td>'.$result['email'].'</td>
                </tr>
                <tr>
                    <th scope="row">Rôle</th>
                    <td>'.$role.'</td>
                </tr>
                <tr>
                    <th scope="row">Newsletter</th>
                    <td>'.$newsletterTOF.'</td>
                </tr>
                <tr>
                    <th scope="row">Gendre</th>
                    <td>'.$gender.'</td>
                </tr>
                <tr>
                    <th scope="row">Date de Naissance</th>
                    <td>'.date('d/m/Y', strtotime($result['birthday'])).'</td>
                </tr>
                <tr>
                    <th scope="row">Adresse</th>
                    <td>'.$result['address'].', '.$result['post_code'].' '.ucwords($result['city']).', '.strtoupper($result['country']).'</td>
                </tr>
                <tr>
                    <th scope="row">Téléphone</th>
                    <td>'.$result['phone'].'</td>
                </tr>
                <tr>
                    <th scope="row">Date d\'inscription</th>
                    <td>'.date('d/m/Y', strtotime($result['date_inserted'])).'</td>
                </tr>
                <tr>
                    <th scope="row">Nombre d\'articles</th>
                    <td>'.$result2[0].'</td>
                </tr>
                <tr>
                    <th class="last" scope="row">Nombre de dons effectués</th>
                    <td class="last">'.$result3[0].'</td>
                </tr>
            </tbody>
        </table>
        </center>';


        
        $dompdf = new Dompdf();
        $dompdf->getOptions()->set('defaultFont', 'Arial');
        $dompdf->setPaper('A4', 'portrait');
        $dompdf->loadHtml($html);
        $dompdf->render();
        $dompdf->stream("export_de_donnees.pdf", array("Attachment" => 1));
        exit;
    }
?>

